import { Component, ElementRef, EventEmitter, OnInit, Output, ViewChild } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { SearchCondition } from '../../models/searchCondition';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit {

  searchCondition: SearchCondition | undefined;
  @Output() emitSearchCondition = new EventEmitter<FormGroup>();
  @ViewChild('checkCategories', { static: true }) checkCategoriesBox!: ElementRef;
  @ViewChild('music', { static: true }) musicBox!: ElementRef;
  @ViewChild('sport', { static: true }) sportBox!: ElementRef;
  @ViewChild('checkGroups', { static: true }) checkGroupsBox!: ElementRef;
  @ViewChild('female', { static: true }) femaleBox!: ElementRef;
  @ViewChild('male', { static: true }) maleBox!: ElementRef;


  searchForm = new FormGroup({
    searchTerm: new FormControl(''),
    music: new FormControl(''),
    sport: new FormControl(''),
    checkCategories: new FormControl(''),
    female: new FormControl(''),
    male: new FormControl(''),
    checkGroups: new FormControl(''),
    period: new FormControl(''),
    termMatch: new FormControl('')
  });


  constructor() { }

  ngOnInit(): void {
  }


  onSubmit(): void {
    this.emitSearchCondition.emit(this.searchForm);
  }

  checkAllCategories(): void { //check both music and sport
    if (this.checkCategoriesBox.nativeElement.checked) {
      this.musicBox.nativeElement.checked = true;
      this.sportBox.nativeElement.checked = true;
    }
    else {
      this.musicBox.nativeElement.checked = false;
      this.sportBox.nativeElement.checked = false;
    }
  }

  checkAllGroups(): void { //check both female and male
    if (this.checkGroupsBox.nativeElement.checked) {
      this.femaleBox.nativeElement.checked = true;
      this.maleBox.nativeElement.checked = true;
    }
    else {
      this.femaleBox.nativeElement.checked = false;
      this.maleBox.nativeElement.checked = false;
    }
  }
}
<form [formGroup]="searchForm" (ngSubmit)="onSubmit()">
  <div class="form-control"><input type="search" placeholder="Type your search term" formControlName="searchTerm" required><button type="submit" [disabled]="!searchForm.valid">Search</button></div>
  <div class="form-control d-flex flex-row"><div><label id="musicLbl" for="m">Music</label><input type="checkbox" id="m" formControlName="music" #music value="music"></div><div><label id="sportLbl" for="s">Sport</label><input type="checkbox" id="s" #sport formControlName="sport" value="sport"></div><div><label id="checkCategoriesLbl" for="cc">Check all</label><input type="checkbox" id="cc" #checkCategories formControlName="checkCategories" (click)="checkAllCategories()" value="allCategories"></div></div>
  <div class="form-control d-flex flex-row"><div><label id="femaleLbl" for="f">Female</label><input type="checkbox" id="f" formControlName="female" #female value="female"></div><div><label id="sportLabl" for="ma">Male</label><input type="checkbox" id="ma" #male formControlName="male" value="male"></div><div><label id="checkGroupsLabl" for="cg">Check all</label><input type="checkbox" id="cg" #checkGroups formControlName="checkGroups" (click)="checkAllGroups()" value="allGroups"></div></div>
  <div lass="form-control d-flex flex-row"><input type="radio" value="5years" formControlName="period">Last 5 years<input type="radio" value="alltime" formControlName="period">All time</div>
  <div class="form-control d-flex flex-row"><input type="radio" value="match exactly" formControlName="termMatch">Match exactly<input type="radio" value="allow fusion" formControlName="termMatch">Allow fusion</div>
</form>
